import React, { Component } from 'react'

export default class SideNav extends Component {
    constructor(props) {
        super(props)

        this.state = {
             open: false
        }
    }

    changeView = () => {
        this.setState({
            open: !this.state.open
        })
    }

    render() {

        const button = <button className="side-nav btn" onClick={this.changeView}> X </button>

        const side = <div onClick={this.changeView} className="side-nav">
                            side nav showing 
                     </div>


        const showing = (this.state.open) ? side : button

        return (
            <div className="nav-container"> 
                {showing}
            </div>
        )
    }
}
