import React, { Component } from 'react'
import SearchBar from './SearchBar'
import NavComponent from './NavComponent'

export default class SearchHome extends Component {
    constructor(props) {
        super(props)

        this.state = {
             current: "us"
        }
    }

    componentDidMount() {

        const usOptions = ["the world","the environement", "the oceans", "the forests", "ourselves"]

        function getNewOption(current) {
            const randomOption = Math.floor(Math.random() * usOptions.length)
            const newer = usOptions[randomOption]
            if(current === newer) {
              return getNewOption(current)
            } else {
                return newer
            }
        }

        let count = 0

        setTimeout(() => {

            let randomTime = Math.random() * 2000 + 1000

            const interval = setInterval(() => {
                    count++
                    randomTime = Math.random() * 2000 + 1000
                 if(count > 5) {
                    this.setState({current:"us"})
                    clearInterval(interval)
                } else {
                    this.setState({
                        current: getNewOption(this.state.current)
                    })
                }
            }, randomTime);
        }, 4000)
    }


    render() {
        return (
            <>
                <div className="psuedo-nav">
                    <NavComponent />       
                </div>


                <div className="search-box-center" > 
                    <div>
                        <div className="titles"> 
                            <p id="title-home" >
                                Sustain.{this.state.current}
                            </p>
                            <p id="sub-title">
                                Connecting you with sustainable businesses
                            </p>
                        </div>
                        <SearchBar />
                    </div>
                </div>

                <div className="footer">

                </div>
            </>
        )
    }
}
