import React, { Component } from 'react'
import BusinessList from './BusinessList'

export default class MapBody extends Component {
    constructor(props) {
        super(props)

        this.state = {
             open: false
        }
    }

    changeView = () => {
        this.setState({
            open: !this.state.open
        })
    }

    render() {


        const googleMap = <div> Map view showing underneath at all times </div>


        const side = <div onClick={this.changeView} className="side-nav">
                            <BusinessList />
                     </div>


        const showing = (this.state.open) ? side : googleMap
        const buttonText = (this.state.open) ? "Show Map View : )" : "Show List View : )"

        return (
            <div className="nav-container"> 
                {showing}
                <button className="btn" id="nav-btn" onClick={this.changeView}> {buttonText} </button>
            </div>
        )
    }
}
