import React, { Component } from 'react'
import SearchBar from './SearchBar'
import NavComponent from './NavComponent'

export default class SearchHome extends Component {
    constructor(props) {
        super(props)

        this.state = {
             current: "us"
        }
    }

    changeDot = (e) => {

    }

    componentDidMount() {
        const usOptions = ["the world","the environement", "the oceans", "the forests", "ourselves"]
        let count = 0

        const randomTime = Math.random() * 4000 + 3000

        const interval = setInterval(() => {
            count++
            randomTime = Math.random() * 4000 + 3000
            if(count > 5) {
                this.setState({current:"us"})
                clearInterval(interval)
            } else {
                const randomOption = Math.floor(Math.random() * usOptions.length)
                this.setState({
                    current: usOptions[randomOption]
                })
            }
        }, randomTime);
    }

    reset = () => {
        this.setState({
            currrent: "us"
        })
    }

    render() {



        return (
            <>
                <div className="psuedo-nav">
                    <NavComponent />       
                </div>


                <div className="search-box-center" > 
                    <div>
                        <div className="titles"> 
                            <p id="title-home" >
                                Sustain.{this.state.current}
                            </p>
                            <p id="sub-title">
                                Connecting with you sustainable businesses
                            </p>
                        </div>
                        <SearchBar />
                    </div>
                </div>

                <div className="footer">

                </div>
            </>
        )
    }
}
