import React from 'react'
import data from '../businesses.json'
import { Form } from 'react-bootstrap'

export default function BusinessList() {

    const fakeRedirect = () => {
        console.log('clicked')
        alert("fake redirect to " )
    }

    const map = data.map((business) => {

        const tagMap = business.businessType.map((bus) => {
            return (<li className="horizontal-li list" key={bus}> 
                        <a href="#"> #{bus} </a>
                    </li>)
        })

        const featured = (business.promoted) ? <span id="promoted">Promoted!</span> : null

        return (
                    <li key={business.distance} className="list side-card borderB">
                        <div className="side-card" onClick={fakeRedirect}>
                            {featured}
                            <h5>
                                {business.name}
                            </h5>
                            <p>
                                {business.address}
                            </p>
                            <div> 
                                <ul className="horizontal">  
                                    {tagMap}
                                </ul> 
                            </div>
                        </div>
                    </li>
            )
        }
    )

    return (
        <>
            <div>

            <Form >
                <Form.Group>
                    <Form.Control type="input" placeholder="Search..." />
                </Form.Group>
            </Form>

            </div>
            <ul>
                {map}
            </ul>
        </>
    )
}
