import React, { Component } from 'react'
import BusinessMap from './BusinessMap'

export default class MapBody extends Component {
    constructor(props) {
        super(props)

        this.state = {
             open: false
        }
    }

    changeView = () => {
        this.setState({
            open: !this.state.open
        })
    }

    render() {

        const button = <button className="btn" id="nav-btn" onClick={this.changeView}> List view please : ) </button>



        const googleMap = <div> Map view showing underneath at all times </div>



        const side = <div onClick={this.changeView} className="side-nav">
                            <BusinessMap />
                     </div>


        const showing = (this.state.open) ? side : button

        return (
            <div className="nav-container"> 
                {showing}
            </div>
        )
    }
}
